var menuTrigger = document.getElementById('menu');
var site = document.querySelector('body');

var navs = document.querySelectorAll('[data-route]');

menuTrigger.addEventListener('click', function(){

  if(site.classList.contains('is-active')) {

    site.classList.remove('is-active');
  } else {

    site.classList.add('is-active');
  }

});
