// Menu

var site = document.querySelector('html');
var menuTrigger = document.getElementById('btn-menu');

menuTrigger.addEventListener('click', function(){

    if(site.classList.contains('open')) {
        site.classList.remove('open'); } else { site.classList.add('open'); }
});

// Form

function prepareEventHandlers() {

    document.forms.formContact.onsubmit = function() {
        if (document.forms.formContact.name.value == "", document.forms.formContact.message.value == "") {
            alert('A  field is empty');
            return false;
        } else {
            return true;
        }
    };
}
window.onload = function() {

    prepareEventHandlers();
}
